import React, {
    useState,
    useEffect,
    useContext
} from 'react';
import NavBar from '../6_navBar/NavBar'
import axios from 'axios'
import { Link } from 'react-router-dom';


const ProfilePage = ()=>{

 const [idUser,setIdUser]=useState([])

 const [updateUser,setUpdateUser]=useState({
    firstname:'',
    lastname:'',
    email:'',
    password:'',
    birthday:'',
    birthday_place:'',
    actual_club:'',
    categorie:'',
    size:'',
    weight:'',
    hobbies:'',
    profil_pic:'',
    role:''
 })

    useEffect(() => {
        fetchDataUser()
    }, [])
    const fetchDataUser = ()=>{
        axios.get(`http://localhost:5000/users/6`)
        .then(res => setIdUser(res.data))
        .catch((err) => console.log(err))
    }

     const updateDataUser = (e) => {
         e.preventDefault()
         axios.put(`http://localhost:5000/users/6`, updateUser)
             .catch((err) => console.log(err))
     }




    return(
        <div>
            <NavBar/>
    <p>{idUser.firstname}</p>
    <p>{idUser.lastname}</p>
    <p>{idUser.email}</p>
    <p>{idUser.password}</p>
    <p>{idUser.birthday}</p>

    <h1>autres champs du user à modifier ou à rentrer</h1>

    <forme onSubmit={updateDataUser}>
        <p>firstname</p>
         <input id="firstname" name="firstname" value={updateUser.firstname} required type="text"
        onChange={(e) => { setUpdateUser({ ...updateUser, firstname: e.target.value }) }} />
        <button type="submit">submit</button>


    </forme>









        </div>
    )
}

export default ProfilePage;
