import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios'
import { Col, Row, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import Injection_images from '../7_injection_images/Injection_images'
import './RegisterPage.css'
import logo from '../../img/logoVert.png'


const RegisterPage = () => {

  const [lastname,setLastname] = useState(null)
  const [firstname,setFirstname] = useState(null)
  const [email,setEmail] = useState(null)    
  const [pseudo, setPseudo] = useState(null)

  // Password and verification
  const [password,setPassword] = useState(null)
  const [confirmePassword, setConfirmePassword]=useState(null)

  const [errorPassword, setErrorPassword] = useState(null)

  const handleFirstNameChange = evt => {
    const newValue = evt.target.value;

    setFirstname(newValue);
  }

  const handleLastNameChange = evt => {
    const newValue = evt.target.value;

    setLastname(newValue);
  }

  const handlePasswordChange = evt => {
    const newValue = evt.target.value;

    setPassword(newValue);
  }
  const handleEmailChange = evt => {
    const newValue = evt.target.value;

    setEmail(newValue);
  }

  const handleConfirmePasswordChange = evt => {
    const newValue = evt.target.value;

    setConfirmePassword(newValue);

  }

  const handleDateChange = (evt) => {
    const newValue = evt.target.value;

    setPseudo(newValue);
  }

const handleClick = (e) =>{
    e.preventDefault()
    if (password === confirmePassword && password.length >= 6 ) {

    axios.post('http://localhost:3001/user/profile',{
        firstname: `${firstname}`,
        lastname: `${lastname}`,
        email: `${email}`,
        password: `${password}`,
        daily_bet: 0,
        pseudo: `${pseudo}`

    })
    .then(function(response){
        console.log(response)
    })
    .catch(function(error){
        console.log(error)
    })}else{
        setErrorPassword('les mots de passe ne correspondent pas, veuillez réessayer')
    }
}

  return (
    <div className="container-register">
      <div className="container-logo">

        <img className="register-logo" src={logo} alt="logo" />
      </div>
      <div className="register-login">
        <h1 className="register-h1">SYNAPSE</h1>
        <h2 className="register-h2">Boostez votre carrière footballistique</h2>

        {/* <h3>Prénom</h3>
            <input placeholder="Prénom"
            id="Prénom"
            name="Prénom"
            // value={e.target.value}
            type="text"
            // onChange={}
            /> */}

        {/*  <h3>Nom</h3>
            <input placeholder="Nom"
            id="Nom"
            name="Nom"
            // value={e.target.value}
            type="text"
            // onChange={}
            /> */}

        {/* <h3>Date de naissance</h3>
            <input placeholder="Date_de_naissance"
            id="Date_de_naissance"
            name="Date_de_naissance"
            // value={e.target.value}
            type="date"
            // onChange={}
            /> */}

        {/*   <h3>email</h3>
            <input placeholder="email"
            id="email"
            name="email"
            // value={e.target.value}
            type="email"
            // onChange={}
            /> */}


        <Form className='form-register' onSubmit={(e)=>handleClick(e)}>  
          <Row form>
            <Col md={11}>
              <FormGroup>
                <Label className="prenom-register" for="prenom">Prénom</Label>
                <Input type="text" name="prenom" id="prenom"  onChange={(evt)=>handleFirstNameChange(evt)} required/>
              </FormGroup>
            </Col>

            <Col md={11}>
              <FormGroup>
                <Label for="Nom">Nom</Label>
                <Input type="text" name="nom" id="Nom" onChange={(evt)=>handleLastNameChange(evt)} required/>
              </FormGroup>
            </Col>

            <Col md={11}>
              <FormGroup>
                <Label for="exampleAddress" onChange={(evt)=>handleDateChange(evt)}>Date de naissance</Label>
                <Input type="text" name="Date_de_naissance" id="Date_de_naissance" />
              </FormGroup>
            </Col>

            <Col md={11}>
              <FormGroup>
                <Label for="email">Adresse e-mail</Label>
                <Input type="text" name="email" id="email"  value={email}  onChange={(evt)=>handleEmailChange(evt)} required/>
              </FormGroup>
            </Col>

            <Col md={11}>
              <FormGroup>
                <Label for="Mot_de_passe">Mot de passe (6 charactères minimun)</Label>
                <Input type="text" name="Mot_de_passe" id="Mot_de_passe" value={password} placeholder="Rentre ton mot de passe..." onChange={(evt)=>handlePasswordChange(evt)} minLength= '6' required/>
              </FormGroup>
            </Col>

            <p className="p-register">Vous acceptez les conditions d'utilisation, la Politique de confidentialité et la Politique relative aux cookies de Synapse.</p>

            <div className="bottom-register">
              <Link to='/login'><button className="button-signin">Accepter et s'inscrire</button></Link>
              <div className="separation">OU</div>
              <button className="button-fb">Continuer avec Facebook</button>
              <div className="register-footer">
                Déjà inscrit(e) ?
                <Link to='/login'> S'identifier</Link>
              </div>
            </div>
          </Row>
        </Form>

      </div>
    </div>
  )
}
export default RegisterPage;
